import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from "react-bootstrap/Modal";
import React, { useState } from 'react';

function converter(props) {
    switch (props.target) {
        case "USD":
            alert(props.sourceVal*0.013)
            return props.sourceVal
        case "Euro":
            alert("Euro")
            break;
    }
}
function ResModal(props) {
    const result = converter(props.target,props.sourceVal)
    return (
        <Modal show={props.isOpen} onHide={props.hideModal}>
            <Modal.Header>You are converting {props.source} to {props.target}</Modal.Header>
            <Modal.Body>{result}</Modal.Body>
            <Modal.Footer>
                <button className="btn btn-primary" onClick={props.hideModal}>Ok</button>
            </Modal.Footer>
        </Modal>
    )
}

export default ResModal
